(191, 'cinematix', 'a:52:{s:9:"logo_path";s:48:"http://www.demo1.diaboliquedesign.com/5/logo.png";s:12:"favicon_path";s:51:"http://www.demo1.diaboliquedesign.com/5/favicon.gif";s:5:"wpbar";s:1:"0";s:8:"widget-1";s:1:"3";s:8:"widget-2";s:1:"4";s:2:"fb";s:23:"http://www.facebook.com";s:9:"analytics";s:0:"";s:4:"s-a1";s:9:"Cinematix";s:4:"s-a2";s:24:"A Social Network Service";s:4:"s-a3";s:137:"Register on our site and start creating profiles, posting messages, making connections, creating and interacting in groups and much more.";s:15:"go-to-notlogged";s:8:"register";s:12:"go-to-logged";s:8:"about-us";s:4:"s-b1";s:27:"A social networking service";s:4:"s-b2";s:17:"How does it work?";s:4:"s-b3";s:334:"Users must register before using the site, after which they may create a personal profile, add other users as friends, and exchange messages, including automatic notifications when they update their profile. Additionally, users may join common-interest user groups, organized by workplace, school or college, or other characteristics.";s:16:"take-a-tour_path";s:48:"http://www.demo1.diaboliquedesign.com/5/tour.png";s:3:"t-1";s:17:"Password Recovery";s:3:"t-2";s:11:"On the Blog";s:3:"t-3";s:13:"On the Forums";s:3:"t-4";s:22:"All rights reserved by";s:3:"t-6";s:2:"or";s:3:"t-7";s:5:"Hello";s:3:"t-8";s:6:"Search";s:3:"t-9";s:7:"Popular";s:4:"t-10";s:6:"Active";s:4:"t-11";s:12:"Alphabetical";s:4:"t-12";s:6:"Newest";s:4:"t-13";s:149:"You can browse all activities here. Use menu below if you want to browse only specific activities like Group Memberships, New Topics, New Members ...";s:4:"t-14";s:45:"A message will be sent to your email address.";s:4:"t-15";s:15:"Blog categories";s:4:"t-16";s:10:"Not Found!";s:4:"t-17";s:11:"Our Members";s:4:"t-19";s:10:"Our Groups";s:4:"t-18";s:45:"Login to your account and check new messages.";s:4:"t-20";s:13:"Related Posts";s:4:"t-21";s:9:"Read more";s:4:"t-22";s:5:"Guest";s:4:"t-23";s:6:"GROUPS";s:5:"t-23a";s:6:"groups";s:4:"t-24";s:6:"FORUMS";s: